import React, {Component} from 'react';
import './App.css';
import CharacterList from './components/CharacterList'
import TeamList from './components/TeamList'
import Nav from './components/Nav'
import {getGryffindor} from './endpoints'

class App extends Component {

  state={
    allStudents:[],
    teamStudents:[]
  }

  componentDidMount(){
    fetch(getGryffindor).then(resp=>resp.json())
    .then(json=>this.setState({allStudents:json[0].members}))
  }

  handleAllStudentsClick=()=>{
    console.log('click')
  }

  render(){
    return (
      <div id="character-container">
        <Nav />
        <CharacterList handleToggle={this.handleAllStudentsClick} allStudents={this.state.allStudents}/>
        <TeamList teamStudents={this.state.teamStudents}/>
      </div>
    )
  }
}

export default App;
