import React from 'react';

class CharacterCard extends React.Component {
  state={
    editForm:false
  }

  handlEditClick=()=>{
    this.setState({editForm:!this.state.editForm})
  }

  render(){
    if(this.state.editForm){
      return (
        <div id="character-card">
        <input type="text" value="FORM" onChange={(e)=>console.log(e.target.value)}/>
        <button onClick={()=>console.log('click')}>Edit Name</button>
        </div>
      )
    }else{
      return (
        <div id="character-card">
        {this.props.studentData.name}
          <div>
          <button onClick={()=>this.props.handleToggle(this.props.studentData)}>Toggle</button>
          <button onClick={this.handlEditClick}>Edit</button>
          </div>
        </div>
      )
    }
  }

}

export default CharacterCard;
