import React from 'react';

class CharacterCard extends React.Component {
  state={
    editForm:false
  }

  handlEditClick=()=>{
    this.setState({editForm:!this.state.editForm})
  }

  render(){
    if(this.state.editForm){
      return (
        <div>Hello
        </div>
      )
    }
  }

}

export default CharacterCard;
