import React from 'react';

class CharacterCard extends React.Component {
  state={
    editForm:false,
    formName:""
  }

  handlEditClick=()=>{
    this.setState({editForm:!this.state.editForm})
  }

  render(){
    if(this.state.editForm){
      return (
        <div id="character-card">
        <input type="text" value={this.props.formName} onChange={(e)=>{
          this.setState({formName:e.target.value})
          this.setState({editForm:true})
        }} />
          <div>
          <button onClick={(e)=>this.props.handleEdit(this.props.studentData, this.state.formName)}>Edit Name</button>
          </div>
        </div>
      )
    }else{
      return (
        <div id="character-card">
        {this.props.studentData.name}
          <div>
          <button onClick={()=>this.props.handleToggle(this.props.studentData)}>Toggle</button>
          <button onClick={this.handlEditClick}>Edit</button>
          </div>
        </div>
      )
    }
  }

}

export default CharacterCard;
